import React, { useEffect, useRef } from 'react';
import { Chart, CategoryScale, LinearScale, BarController, BarElement, Tooltip } from 'chart.js';

Chart.register(CategoryScale, LinearScale, BarController, BarElement, Tooltip);

function Overview({ transactions }) {
  const chartRef = useRef(null);
  const chartInstanceRef = useRef(null);

  useEffect(() => {
    if (chartRef.current) {
      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
      }

      const ctx = chartRef.current.getContext('2d');

      // Calculate the end date as the current month
      const today = new Date();
      const endDate = new Date(today.getFullYear(), today.getMonth() + 1, 0); // Last day of the current month

      // Calculate the start date for the previous 12 months
      const startDate = new Date(today.getFullYear(), today.getMonth() - 11, 1); // First day of the 12th month ago

      // Generate the labels for the previous 12 months
      const labels = [];
      for (let i = 0; i < 12; i++) {
        const date = new Date(startDate.getFullYear(), startDate.getMonth() + i);
        labels.push(date.toLocaleString('default', { month: 'short' }));
      }

      // Filter transactions for the previous 12 months
      const filteredTransactions = transactions.filter(
        (transaction) => {
          const transactionDate = new Date(transaction.date);
          return transactionDate >= startDate && transactionDate <= endDate;
        }
      );

      // Group transactions by month and calculate total income and expenses for each month
      const groupedData = Array.from({ length: 12 }, (_, i) => {
        const month = (startDate.getMonth() + i) % 12;
        return {
          Income: filteredTransactions
            .filter(transaction => new Date(transaction.date).getMonth() === month && transaction.type === 'Income')
            .reduce((acc, transaction) => acc + transaction.amount, 0),
          expenses: filteredTransactions
            .filter(transaction => new Date(transaction.date).getMonth() === month && transaction.type === 'expense')
            .reduce((acc, transaction) => acc + transaction.amount, 0)
        };
      });

      const IncomeData = Array.from({ length: 12 }, (_, index) => groupedData[index]?.Income || 0);
      const expenseData = Array.from({ length: 12 }, (_, index) => groupedData[index]?.expenses || 0);

      chartInstanceRef.current = new Chart(ctx, {
        type: 'bar',
        data: {
          labels: labels,
          datasets: [
            {
              label: 'Income',
              data: IncomeData,
              backgroundColor: '#b996ff',
              barThickness: 20,
              barSpacing: 3,
            },
            {
              label: 'Expenses',
              data: expenseData,
              backgroundColor: '#e0deff',
              barThickness: 20,
              barSpacing: 3,
            },
          ],
        },
         options: {
    scales: {
      x: {
        grid: {
          display: false, // Hide vertical grid lines
        },
      },
      y: {

        grid: {
          borderDash: 3, // Set dotted lines for horizontal grid
        },
        ticks: {
          callback: function(value, index, values) {
            // Format numbers to show as 'k'
            if (Math.abs(value) >= 1000) {
              return value/1000 + 'k';
            } else {
              return value;
            }
          },
          stepSize: 2000,
        },
      },
    },
  },
});



    }
  }, [transactions]);

  return (
    <div className="overview">
      <div className="split-header header">
        <h1>Overview</h1>
        <div className="overview__chart-controls"></div>
      </div>

      <div className="overview__chart-container">
        <canvas ref={chartRef} />
      </div>
    </div>
  );
}

export default Overview;
