import React, { useState } from 'react';


function Summary({ sampleTransactions, transactions }) {

// Get the current date
const currentDate = new Date();

// Get the first day of the current month
const firstDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);

// Get the last day of the current month
const lastDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0);

// Filter transactions for the current month
const currentMonthTransactions = sampleTransactions.filter(transaction => {
  const transactionDate = new Date(transaction.date);
  return transactionDate >= firstDayOfMonth && transactionDate <= lastDayOfMonth;
});

// Group transactions by category and calculate total amount for each category
const expenseSummary = {};
currentMonthTransactions.forEach(transaction => {
  if (transaction.type === 'expense') {
    if (!expenseSummary[transaction.category]) {
      expenseSummary[transaction.category] = 0;
    }
    expenseSummary[transaction.category] += transaction.amount;
  }
});

console.log(expenseSummary);

    return (
      <div className="summary">
        <div className="header">
          <h1>Expense Summary</h1>
        </div>
        <div className="summary__content">
          <h4>Total Expenses</h4>
          <h3>$3,400</h3>
        </div>
        <div className="summary__expense">
        {Object.keys(expenseSummary).length > 0 && (
      <h1>{Object.keys(expenseSummary)[0]}</h1>
    )}
        </div>
      </div>
    );
}

export default Summary;
